object LocalStorageManager {
    private const val PREFS_NAME = "playlist_prefs"
    private const val LAST_PLAY_TRACK_KEY = "last_played_track"
    private const val NOW_PLAYING_LIST_KEY = "now_playing_list"
    private const val PLAYLIST_KEY = "playlist_key"

    private const val TOKEN_PREFS_NAME = "token_prefs"
    private const val ACCESS_TOKEN_KEY = "access_token"
    private const val REFRESH_TOKEN_KEY = "refresh_token"

    private fun getPreferences(context: Context): SharedPreferences =
        context.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)

    private fun getTokenPreferences(context: Context): SharedPreferences =
        context.getSharedPreferences(TOKEN_PREFS_NAME, Context.MODE_PRIVATE)

    // 액세스 토큰
    fun saveAccessToken(context: Context, token: String) {
        val prefs = getTokenPreferences(context)
        prefs.edit().putString(ACCESS_TOKEN_KEY, token).apply()
    }

    fun getAccessToken(context: Context): String? {
        return getTokenPreferences(context).getString(ACCESS_TOKEN_KEY, null)
    }

    // 리프레쉬 토큰
    fun saveRefreshToken(context: Context, token: String) {
        val prefs = getTokenPreferences(context)
        prefs.edit().putString(REFRESH_TOKEN_KEY, token).apply()
    }

    fun getRefreshToken(context: Context): String? {
        return getTokenPreferences(context).getString(REFRESH_TOKEN_KEY, null)
    }

    // 토큰 삭제
    fun clearTokens(context: Context) {
        val prefs = getTokenPreferences(context)
        prefs.edit().remove(ACCESS_TOKEN_KEY).remove(REFRESH_TOKEN_KEY).apply()
    }


    // 디폴트 재생 목록
    fun savePlaylist(context: Context, playlist: List<MusicDetail>) {
        val json = Gson().toJson(playlist)
        getPreferences(context).edit().putString(PLAYLIST_KEY, json).apply()
    }

    fun loadPlaylist(context: Context): List<MusicDetail> {
        val json = getPreferences(context).getString(PLAYLIST_KEY, "[]")
        val type = object : TypeToken<List<MusicDetail>>() {}.type
        return Gson().fromJson(json, type)
    }

