    fun initializeIfNeeded(context: Context) {
        if (!getPreferences(context).contains(PLAYLIST_KEY)) {
            saveDummyData(context)
        }
    }

    fun saveSelectedTrackToTop(context: Context, selectedTrack: MusicDetail) {
        // 기존 플레이리스트 로드
        val playlist = loadPlaylist(context).toMutableList()

        // 선택된 곡을 리스트의 맨 앞으로 추가
        playlist.add(0, selectedTrack)

        // 업데이트된 리스트를 다시 저장
        savePlaylist(context, playlist)
    }


    // 현재 재생 중인 재생 목록
    fun saveNowPlayingList(context: Context, nowPlayingList: NowPlayingList) {
        val json = Gson().toJson(nowPlayingList)
        getPreferences(context).edit().putString(NOW_PLAYING_LIST_KEY, json).apply()
    }

    fun getNowPlayingList(context: Context): NowPlayingList {
        val json = getPreferences(context).getString(NOW_PLAYING_LIST_KEY, null)
        return if (json != null) {
            try {
                Gson().fromJson(json, NowPlayingList::class.java)
            } catch (e: Exception) {
                e.printStackTrace()
                NowPlayingList("default", -1, emptyList()) // 기본값 반환
            }
        } else {
            NowPlayingList("default", -1, emptyList()) // 기본값 반환
        }
    }


    // 마지막으로 재생한 곡
    fun saveLastPlayedTrack(context: Context, track: MusicDetail, index: Int) {
        val lastPlayedTrack = LastPlayedTrack(
            index = index,
            trackId = track.trackId,
            title = track.title,
            composer = track.composer,
            description = track.description,
            tags = track.tags,
            videoId = track.videoId,
            imageUrl = track.imageUrl,
            vrImageUrl = track.vrImageUrl,
            thumbnailUrl = track.thumbnailUrl,
            hapticTime = track.hapticTime,
            hapticIntensity = track.hapticIntensity
        )
        val json = Gson().toJson(lastPlayedTrack)
        getPreferences(context).edit().putString(LAST_PLAY_TRACK_KEY, json).apply()
    }

    fun saveLastPlayedTrackInPlaylist(context: Context, track: LastPlayedTrack) {
        val json = Gson().toJson(track)
        getPreferences(context).edit().putString(LAST_PLAY_TRACK_KEY, json).apply()
    }

    fun getLastPlayedTrack(context: Context): LastPlayedTrack? {
        val json = getPreferences(context).getString(LAST_PLAY_TRACK_KEY, null)
        return json?.let { Gson().fromJson(it, LastPlayedTrack::class.java) }
    }

    // 다음 곡
    fun loadNextTrack(context: Context): Pair<MusicDetail?, Int?> {
        val nowPlayingList = getNowPlayingList(context)
        if (nowPlayingList.playlist.isEmpty()) return Pair(null, null)

        val currentIndex = getLastPlayedTrack(context)?.index ?: -1
        val newIndex = if (currentIndex in 0 until nowPlayingList.playlist.size - 1) {
            currentIndex + 1
        } else {
            0
        }

        // 새 인덱스의 PlaylistItem을 가져와서 마지막 재생한 곡으로 저장
        val nextTrack = nowPlayingList.playlist[newIndex]
        saveLastPlayedTrack(context, nextTrack, newIndex)

        return Pair(nextTrack, newIndex)
    }


    // 이전 곡
    fun loadPreviousTrack(context: Context): Pair<MusicDetail?, Int?> {
        val nowPlayingList = getNowPlayingList(context)
        if (nowPlayingList.playlist.isEmpty()) return Pair(null, null)

        val currentIndex = getLastPlayedTrack(context)?.index ?: -1
        val newIndex = if (currentIndex in 1 until nowPlayingList.playlist.size) {
            currentIndex - 1
        } else {
            nowPlayingList.playlist.size - 1
        }

        // 새 인덱스의 PlaylistItem을 가져와서 마지막 재생한 곡으로 저장
        val previousTrack = nowPlayingList.playlist[newIndex]
        saveLastPlayedTrack(context, previousTrack, newIndex)

        return Pair(previousTrack, newIndex)
    }

